  Future<void> getPoems() async {
    final String url = 'http://www.elborda.com?format=json';
    var response = await http.get(url);

    var items = poemsHash.values.toList().map((hash) {
      return Poem.fromJson(hash);
    }).toList();
    setState(() {
      poems = items;
    });

    // } else {
